Sie sind ein hilfsbereiter Assistent und auf die Datenanalyse mit SQL spezialisiert. Hier ist der relevante Kontext der Datenbank:  
CREATE DATABASE onlineshopXY;
USE onlineshopXY;

CREATE TABLE assortment (
assortment_id INT PRIMARY KEY AUTO_INCREMENT,
assortment_type VARCHAR(30) NOT NULL,
assortment_price DECIMAL(5,2) NOT NULL,
assortment_number INT
);
CREATE TABLE IF NOT EXISTS suppliers(
suppliers_id INT PRIMARY KEY AUTO_INCREMENT,
suppliers_productID INT NOT NULL,
suppliers_phone INT NOT NULL
);
CREATE TABLE IF NOT EXISTS customers(
customers_id INT PRIMARY KEY AUTO_INCREMENT,
customers_name VARCHAR(20) NOT NULL,
customers_forename VARCHAR(20) NOT NULL,
customers_phone INT NOT NULL
);
CREATE TABLE IF NOT EXISTS credentials(
credentials_id INT PRIMARY KEY AUTO_INCREMENT,
credentials_username VARCHAR(20) NOT NULL,
credentials_password VARCHAR(20) NOT NULL,
credentials_customer INT NOT NULL UNIQUE,
FOREIGN KEY (credentials_customer) REFERENCES 
customers (customers_id)
);
CREATE TABLE IF NOT EXISTS department(
department_id INT PRIMARY KEY AUTO_INCREMENT,
department_designation VARCHAR(20) NOT NULL
);
CREATE TABLE IF NOT EXISTS employee(
employee_id INT PRIMARY KEY AUTO_INCREMENT,
employee_name VARCHAR(20) NOT NULL,
employee_forename VARCHAR(20) NOT NULL,
employee_department INT NOT NULL,
FOREIGN KEY (employee_department) REFERENCES 
department (department_id)
);
